/** @format */

import React, { Fragment, useEffect, useState } from "react";
import {
  Box,
  Table,
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  Checkbox,
} from "@mui/material";
import { LinkButton } from "../../styles/styles";
import axios from "axios";
import { CONFIG } from "../../api/MatterApi";
import CircleIcon from "@mui/icons-material/Circle";
import { GetDate } from "../../utils/ActionAlerts";

const MatterListLong = () => {
  const [matters, setMatters] = useState([]);

  const [status, setStatus] = useState("");
  const [loading, setLoading] = useState(true);

  const FetchData = () => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/api/create-matter/`, CONFIG)
      .then((res) => {
        setLoading(false);
        setStatus(res.statusText);
        setMatters(res.data);
      })
      .catch((err) => {
        setLoading(false);
        setStatus(err.statusText);
      });
  };
  useEffect(() => {
    FetchData();
  }, []);

  return (
    <Fragment>
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Matter Tasks</TableCell>
              <TableCell>Matter Name</TableCell>
              <TableCell>Matter Contact</TableCell>
              <TableCell>Matter Type</TableCell>
              <TableCell>Bill Rate</TableCell>
              <TableCell>Last Action</TableCell>
              <TableCell>Next Action</TableCell>
              <TableCell>Assigned to</TableCell>
              <TableCell>Billable</TableCell>
              <TableCell>Open Invoice</TableCell>
              <TableCell>Trust Account</TableCell>
              <TableCell>Last Payment</TableCell>
            </TableRow>
            {matters?.map((matter) => {
              <TableRow id={matter.id}>
                <TableCell>
                  <Checkbox />
                </TableCell>
                <TableCell>Matter Tasks</TableCell>
                <TableCell>Matter Name</TableCell>
                <TableCell>Matter Contact</TableCell>
                <TableCell>Matter Type</TableCell>
                <TableCell>Bill Rate</TableCell>
                <TableCell>Last Action</TableCell>
                <TableCell>Next Action</TableCell>
                <TableCell>Assigned to</TableCell>
                <TableCell>Billable</TableCell>
                <TableCell>Open Invoice</TableCell>
                <TableCell>Trust Account</TableCell>
                <TableCell>Last Payment</TableCell>
              </TableRow>;
            })}
          </TableHead>
        </Table>
      </TableContainer>
    </Fragment>
  );
};

export default MatterListLong;
