/** @format */

import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import {
  Typography,
  Button,
  Grid,
  Checkbox,
  FormControlLabel,
} from "@mui/material";

const ManageCategory = () => {
  const [category, setCategory] = useState(null);
  useEffect(() => {
    const config = {
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    };
    const res = axios.get(
      `${process.env.REACT_APP_API_URL}/api/category`,
      config,
    );
    setCategory(res.data);
  }, []);

  return (
    <Fragment>
      <Grid container>
        <Grid item xs={8}>
          <Typography component="h3" variant="h5">
            Manage Category, Sub-category, Classification
          </Typography>
        </Grid>
        <Grid item xs={4}>
          <Button
            variant="contained"
            sx={{
              float: "right",
              borderRadius: "0.5rem",
              paddingInline: "1.5rem",
            }}
          >
            Delete Checked
          </Button>
        </Grid>
        <Grid item xs={12}>
          <Grid container spacing={5} mt={1}>
            <Grid item xs={4}>
              <Button
                variant="contained"
                fullWidth
                sx={{
                  float: "right",
                  borderRadius: "0.5rem",
                  paddingInline: "1.5rem",
                }}
              >
                + New Category
              </Button>
            </Grid>
            <Grid item xs={4}>
              <Button
                variant="contained"
                fullWidth
                sx={{
                  float: "right",
                  borderRadius: "0.5rem",
                  paddingInline: "1.5rem",
                }}
              >
                + New Category
              </Button>
              <FormControlLabel
                control={
                  <Checkbox
                    name="billable"
                    value="billable"
                    onChange={(e) => onChange(e)}
                    id="billable"
                  />
                }
                label="use Operating Balance"
              />
            </Grid>
            <Grid item xs={4}>
              <Button
                variant="contained"
                fullWidth
                sx={{
                  float: "right",
                  borderRadius: "0.5rem",
                  paddingInline: "1.5rem",
                }}
              >
                + New Category
              </Button>
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </Fragment>
  );
};

export default ManageCategory;
