/** @format */
import React, { Fragment, useEffect, useState } from "react";
import {
  TextField,
  Switch,
  FormControl,
  Select,
  MenuItem,
  InputLabel,
  FormControlLabel,
  Box,
  Grid,
  Button,
  Typography,
} from "@mui/material";

import { LocalizationProvider, DateRangePicker, DatePicker } from "@mui/lab";
import AdapterDateFns from "@mui/lab/AdapterDateFns";
import axios from "axios";
import SaveIcon from "@mui/icons-material/Save";
import { useToggle } from "../../context/useToggle";
import { CONFIG } from "../../api/MatterApi";

import { ActionAlerts, GetDate } from "../../utils/ActionAlerts";

const CreateTask = () => {
  const [date, setDate] = useState(new Date("2021-12-01T21:11:54"));
  const [bill, setBill] = useToggle(true);
  const [matterData, setMatterData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isPrivate, setIsPrivate] = useToggle(true);

  const [taskData, setTaskData] = useState({
    matter_no: 0,
    matter_contact: "",
    matter: "",
    billable: true,
    is_private: true,
    task: "",
    dueDate: "",
    assign_to: "",
    detail: "",
  });

  const {
    matter_no,
    matter_contact,
    matter,
    billable,
    is_private,
    task,
    dueDate,
    assign_to,
    detail,
  } = taskData;

  const onChange = (e) =>
    setTaskData({ ...matterData, [e.target.name]: e.target.value });
  const FetchData = () => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/api/matter/`, CONFIG)
      .then((res) => {
        console.log(res.data);
        setLoading(false);
        setMatterData(res.data);
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
      });
  };
  useEffect(() => {
    FetchData();
  }, []);
  const onSubmit = (e) => {
    let due_date = GetDate(dueDate);
    e.preventDefault();
  };
  return (
    <Fragment>
      <Box
        component="form"
        Validate
        sx={{
          "& .MuiTextField-root": { m: 1 },
          "& .MuiSelect-root": { m: 1 },
        }}
        onSubmit={(e) => onSubmit(e)}
      >
        <Typography component="h3" variant="h4">
          Create New Task
        </Typography>
        <Grid container spacing={2} p={2}>
          <Grid item xs={6}>
            <FormControl fullWidth>
              <InputLabel id="demo-simple-select-label">Matter #</InputLabel>
              <Select
                size="small"
                fullWidth
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={matter}
                label="matter_id"
                onChange={(e) => onChange(e)}
              >
                {!loading
                  ? matterData?.map((data) => (
                      <MenuItem id={data.id} value={data.id}>
                        {data.id}
                      </MenuItem>
                    ))
                  : null}
              </Select>
            </FormControl>
            <FormControl fullWidth>
              <InputLabel id="demo-simple-select-label">Matter </InputLabel>
              <Select
                size="small"
                fullWidth
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={matter}
                label="matter_id"
                onChange={(e) => onChange(e)}
              >
                {!loading
                  ? matterData?.map((data) => (
                      <MenuItem id={data.id} value={data.id}>
                        {data.matter_name}
                      </MenuItem>
                    ))
                  : null}
              </Select>
            </FormControl>
            <TextField
              fullWidth
              size="small"
              margin="normal"
              variant="outlined"
              name="task"
              label="Task"
              type="task"
              value={task}
              onChange={(e) => onChange(e)}
              id="task"
              autoComplete="task"
            />
            <Box ml={2} mt={1}>
              <Button variant="contained">+ Attach File</Button>
            </Box>
          </Grid>
          <Grid item xs={6}>
            <FormControl fullWidth>
              <InputLabel id="demo-simple-select-label">
                Matter Contact
              </InputLabel>
              <Select
                size="small"
                fullWidth
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={matter}
                label="matter_id"
                onChange={(e) => onChange(e)}
              >
                {!loading
                  ? matterData?.map((data) => (
                      <MenuItem id={data.id} value={data.id}>
                        {data.contact}
                      </MenuItem>
                    ))
                  : null}
              </Select>
            </FormControl>
            <Box p={1}>
              <FormControlLabel
                fullWidth
                size="large"
                label="Billable"
                labelPlacement="start"
                control={
                  <Switch
                    size="large"
                    checked={bill}
                    onChange={() => setBill(bill)}
                  />
                }
              />
              <FormControlLabel
                fullWidth
                size="large"
                label="Private"
                labelPlacement="start"
                control={
                  <Switch
                    size="large"
                    checked={isPrivate}
                    onChange={() => setIsPrivate(isPrivate)}
                  />
                }
              />
            </Box>
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                inputFormat="yyyy-MM-dd"
                mask="____-__-__"
                label="Open Date"
                value={dueDate}
                name="open"
                id="open"
                onChange={(e) => setDueDate(e)}
                renderInput={(params) => (
                  <TextField fullWidth size="small" {...params} />
                )}
              />
            </LocalizationProvider>

            <TextField
              fullWidth
              size="small"
              margin="normal"
              variant="outlined"
              name="assign_to"
              label="Assign to"
              type="assign_to"
              value={assign_to}
              onChange={(e) => onChange(e)}
              id="assign_to"
              autoComplete="assign_to"
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              fullWidth
              multiline
              rows={5}
              size="small"
              margin="normal"
              variant="outlined"
              name="detail"
              label="Detail"
              type="text"
              value={detail}
              onChange={(e) => onChange(e)}
              id="detail"
            />

            <Button
              variant="contained"
              type="submit"
              sx={{ float: "right" }}
              endIcon={<SaveIcon />}
            >
              Save
            </Button>
          </Grid>
        </Grid>
      </Box>
    </Fragment>
  );
};

export default CreateTask;
