import React,{useState, useEffect} from 'react';

import { connect } from 'react-redux';

// import DashboardNavbar from '../Navbars/DashboardNavbar';


const Dashboard = ({user}) => {
    const [localuser, setLocalUser] = useState({
        username: 'Guest',
        is_firm: false,
        is_lawyer: false,
        is_client: true
    })

   useEffect(() => {
       if(user){
       setLocalUser({
        username: user.username,
        is_firm: user.is_firm,
        is_lawyer: user.is_lawyer,
        is_client: user.is_client
       })

    }      
   }, [user])
    const dashboardRedirect =() =>{
        if (localuser.is_firm){
            return <h1>Firm user {localuser.username}</h1>
        }
        else if(localuser.is_client){
            return <h1>Client user {localuser.username}</h1>
        }
        else if(localuser.is_lawyer){
            return <h1>Lawyer user {localuser.username}</h1>
        }
        else{
            return <h1>Admin user {localuser.username}</h1>
        }

    }
    return (
        <>
             {dashboardRedirect()}
        </>
    )
}
const mapStateToProps = state => ({
    // isAuthenticated: state.auth.isAuthenticated
    user: state.auth.user,    
  });


export default connect(mapStateToProps)(Dashboard)
