/** @format */

import React, { Fragment, useState } from "react";

import {
  Box,
  FormLabel,
  FormControlLabel,
  Radio,
  RadioGroup,
  FormControl,
  Checkbox,
  Typography,
  TextField,
} from "@mui/material";
import SaveIcon from "@mui/icons-material/Save";
import { LocalizationProvider, DateRangePicker } from "@mui/lab";
import AdapterDateFns from "@mui/lab/AdapterDateFns";

import { useToggle } from "../../context/useToggle";

const InvoicePayment = () => {
  const [dateRange, setDateRange] = React.useState([null, null]);
  const [invoiceData, setInvoiceData] = useState({
    payment_type: "",
    date: "",
    retainer: "",
    payment_method: "",
    date_range: "",
    menu: "",
    bank_account: "",
    payment_method: "",
    payment: "",
  });

  const {
    payment_type,
    date,
    retainer,
    payment_methond,
    date_range,
    menu,
    bank_account,
    payment_method,
    payment,
  } = invoiceData;
  const onChange = (e) =>
    setInvoiceData({ ...invoiceData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
  };
  return (
    <Fragment>
      <Box
        component="form"
        Validate
        sx={{
          "& .MuiTextField-root": { m: 1, width: "15rem" },
        }}
        onSubmit={(e) => onSubmit(e)}
      >
        <FormControl component="fieldset">
          <RadioGroup
            row
            aria-label="payment_type"
            defaultValue="cash"
            name="payment_type"
            onChange={(e) => onChange(e)}
            id="payment_type"
            autoComplete="payment_type"
          >
            <FormControlLabel value="cash" control={<Radio />} label="Cash" />
            <FormControlLabel value="cc" control={<Radio />} label="CC" />
            <FormControlLabel value="wire" control={<Radio />} label="Wire" />
            <FormControlLabel value="check" control={<Radio />} label="Check" />
          </RadioGroup>
        </FormControl>
        <TextField
          size="small"
          id="date"
          label="date"
          type="date"
          InputLabelProps={{
            shrink: true,
          }}
        />
        <FormControlLabel
          value="retainer"
          control={<Checkbox />}
          label="Retainer"
        />
        <FormControlLabel
          value="payment"
          control={<Checkbox />}
          label="Payment"
        />
        <LocalizationProvider dateAdapter={AdapterDateFns}>
          <Typography sx={{ mt: 2, mb: 1 }}> Date Range </Typography>
          <DateRangePicker
            calendars={1}
            value={dateRange}
            onChange={(newValue) => {
              setDateRange(newValue);
            }}
            renderInput={(startProps, endProps) => (
              <Fragment>
                <TextField size="small" {...startProps} />
                <Box sx={{ mx: 2 }}> to </Box>
                <TextField size="small" {...endProps} />
              </Fragment>
            )}
          />
        </LocalizationProvider>
        <TextField
          size="small"
          margin="normal"
          variant="outlined"
          name="menu"
          label="menu"
          type="text"
          onChange={(e) => onChange(e)}
          id="menu"
          autoComplete="menu"
        />
        <TextField
          size="small"
          margin="normal"
          variant="outlined"
          name="bank_account"
          label="bank_account"
          type="text"
          onChange={(e) => onChange(e)}
          id="bank_account"
          autoComplete="bank_account"
        />
        <TextField
          size="small"
          margin="normal"
          variant="outlined"
          name="payment_method"
          label="payment_method"
          type="text"
          onChange={(e) => onChange(e)}
          id="payment_method"
          autoComplete="payment_method"
        />
        <FormControl component="fieldset">
          <RadioGroup
            row
            aria-label="payment"
            defaultValue="cash"
            name="payment"
            onChange={(e) => onChange(e)}
            id="payment"
            autoComplete="payment"
          >
            <FormControlLabel value="cash" control={<Radio />} label="Cash" />
            <FormControlLabel value="cc" control={<Radio />} label="CC" />
          </RadioGroup>
        </FormControl>
      </Box>
    </Fragment>
  );
};

export default InvoicePayment;
