/** @format */
import React, { Fragment, useEffect, useState } from "react";
import {
  TextField,
  Switch,
  FormControl,
  Select,
  MenuItem,
  InputLabel,
  FormControlLabel,
  Box,
  Grid,
  Button,
  Typography,
} from "@mui/material";

import { LocalizationProvider, DateRangePicker, DatePicker } from "@mui/lab";
import AdapterDateFns from "@mui/lab/AdapterDateFns";
import axios from "axios";
import SaveIcon from "@mui/icons-material/Save";
import { useToggle } from "../../context/useToggle";
import { CONFIG } from "../../api/MatterApi";

import { ActionAlerts, GetDate } from "../../utils/ActionAlerts";

const CreateTask = () => {
  const [dueDate, setDueDate] = useState(new Date("2021-12-01T21:11:54"));
  const [bill, setBill] = useToggle(true);
  const [matterData, setMatterData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isPrivate, setIsPrivate] = useToggle(true);

  const [selectedFile, setSelectedFile] = useState();
  const [isFilePicked, setIsFilePicked] = useState(false);

  const uploadFile = (event) => {
    setSelectedFile(event.target.files[0]);
    setIsFilePicked(true);
  };

  const [taskData, setTaskData] = useState({
    matter_id: 0,
    matter_contact: "",
    matter: "",
    task: "",
    assign_to: "",
    detail: "",
  });

  const { matter_id, matter_contact, matter, task, assign_to, detail } =
    taskData;

  const onChange = (e) =>
    setTaskData({ ...matterData, [e.target.name]: e.target.value });
  const FetchData = () => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/api/matter/`, CONFIG)
      .then((res) => {
        console.log(res.data);
        setLoading(false);
        setMatterData(res.data);
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
      });
  };
  useEffect(() => {
    FetchData();
  }, []);
  const onSubmit = (e) => {
    e.preventDefault();

    let due_at = GetDate(dueDate);
    let is_billable = bill;
    let is_private = isPrivate;
    let file = " ";
    const body = JSON.stringify({
      matter_id,
      matter_contact,
      matter,
      task,
      file,
      is_billable,
      is_private,
      due_at,
      assign_to,
      detail,
    });
    axios
      .post(`${process.env.REACT_APP_API_URL}/api/new-task/`, body, CONFIG)
      .then((res) => {
        <ActionAlerts value={{ status: res.statusText, message: "Success" }} />;
      })
      .catch((err) => {
        <ActionAlerts value={{ status: err.statusText, message: "Failed" }} />;
      });
    console.log(body);
  };
  return (
    <Fragment>
      <Box
        component="form"
        Validate
        sx={{
          "& .MuiTextField-root": { m: 1 },
          "& .MuiSelect-root": { m: 1 },
        }}
        onSubmit={(e) => onSubmit(e)}
      >
        <Typography component="h3" variant="h4">
          Create New Task
        </Typography>
        <Grid container spacing={2} p={2}>
          <Grid item xs={6}>
            <TextField
              fullWidth
              size="small"
              margin="normal"
              variant="outlined"
              name="matter_id"
              label="matter_id"
              type="text"
              value={matter_id}
              onChange={(e) => onChange(e)}
              id="matter_id"
              autoComplete="matter_id"
            />
            <TextField
              fullWidth
              size="small"
              margin="normal"
              variant="outlined"
              name="matter"
              label="Matter"
              type="text"
              value={matter}
              onChange={(e) => onChange(e)}
              id="matter"
              autoComplete="matter"
            />
            <TextField
              fullWidth
              size="small"
              margin="normal"
              variant="outlined"
              name="task"
              label="Task"
              type="text"
              value={task}
              onChange={(e) => onChange(e)}
              id="task"
              autoComplete="task"
            />
            <Box ml={2} mt={1}>
              <TextField
                type="file"
                name="file"
                onChange={(e) => onChange(e)}
              />
              <Button variant="contained" onClick={uploadFile}>
                + Upload File
              </Button>
              {isFilePicked ? selectedFile.name : null}
            </Box>
          </Grid>
          <Grid item xs={6}>
            <TextField
              fullWidth
              size="small"
              margin="normal"
              variant="outlined"
              name="matter_contact"
              label="Matter Contact"
              type="text"
              value={matter_contact}
              onChange={(e) => onChange(e)}
              id="task"
              autoComplete="matter_contact"
            />
            <Box p={1}>
              <FormControlLabel
                fullWidth
                size="large"
                label="Billable"
                labelPlacement="start"
                control={
                  <Switch
                    size="large"
                    checked={bill}
                    onChange={() => setBill(bill)}
                  />
                }
              />
              <FormControlLabel
                fullWidth
                size="large"
                label="Private"
                labelPlacement="start"
                control={
                  <Switch
                    size="large"
                    checked={isPrivate}
                    onChange={() => setIsPrivate(isPrivate)}
                  />
                }
              />
            </Box>
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                inputFormat="yyyy-MM-dd"
                mask="____-__-__"
                label="Due Date"
                value={dueDate}
                name="dueDate"
                id="dueDate"
                onChange={(e) => setDueDate(e)}
                renderInput={(params) => (
                  <TextField fullWidth size="small" {...params} />
                )}
              />
            </LocalizationProvider>

            <TextField
              fullWidth
              size="small"
              margin="normal"
              variant="outlined"
              name="assign_to"
              label="Assign to"
              type="assign_to"
              value={assign_to}
              onChange={(e) => onChange(e)}
              id="assign_to"
              autoComplete="assign_to"
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              fullWidth
              multiline
              rows={5}
              size="small"
              margin="normal"
              variant="outlined"
              name="detail"
              label="Detail"
              type="text"
              value={detail}
              onChange={(e) => onChange(e)}
              id="detail"
            />

            <Button
              variant="contained"
              type="submit"
              sx={{ float: "right" }}
              endIcon={<SaveIcon />}
            >
              Save
            </Button>
          </Grid>
        </Grid>
      </Box>
    </Fragment>
  );
};

export default CreateTask;
