/** @format */

import React, { Fragment } from "react";
import { Box, Switch, Menu, MenuItem } from "@mui/material";
import { LinkButton } from "../../styles/styles";

import { useToggle } from "../../context/useToggle";

const Filters = () => {
  const [paid, setPaid] = useToggle(false);
  const label = { inputProps: { "aria-label": "Switch demo" } };

  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <Fragment>
      <Box p={3}>
        <LinkButton to="/create-invoice">Create Invoice</LinkButton>
        <LinkButton to="/create-invoice">Receive Money</LinkButton>
        <Switch
          checked={paid}
          onChange={() => setPaid(paid)}
          {...label}
          // defaultChecked
        />
        <Button
          id="basic-button"
          aria-controls="basic-menu"
          aria-haspopup="true"
          aria-expanded={open ? "true" : undefined}
          onClick={handleClick}
        >
          Dashboard
        </Button>
        <Menu
          id="demo-positioned-menu"
          aria-labelledby="demo-positioned-button"
          anchorEl={anchorEl}
          open={open}
          onClose={handleClose}
          anchorOrigin={{
            vertical: "top",
            horizontal: "left",
          }}
          transformOrigin={{
            vertical: "top",
            horizontal: "left",
          }}
        >
          <MenuItem onClick={handleClose}>Profile</MenuItem>
          <MenuItem onClick={handleClose}>My account</MenuItem>
          <MenuItem onClick={handleClose}>Logout</MenuItem>
        </Menu>
      </Box>
    </Fragment>
  );
};

export default Filters;
