/** @format */

import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import {
  Typography,
  Button,
  Grid,
  Box,
  Checkbox,
  FormControlLabel,
} from "@mui/material";
import ClearIcon from "@mui/icons-material/Clear";

import { Get } from "../../utils/OperationCURD";
const CONFIG = {
  headers: {
    "Content-Type": "application/json",
    Accept: "application/json",
  },
};
const ManageCategory = () => {
  const [category, setCategory] = useState([]);
  const [subCategory, setSubCategory] = useState([]);
  const [classification, setClassification] = useState([]);

  const cat = Get("category");
  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/api/category`, CONFIG)
      .then((res) => {
        setCategory(res.data);
      })
      .catch((err) => {
        console.log(err.status);
      });
  }, []);

  return (
    <Fragment>
      <Grid container>
        <Grid item xs={8}>
          <Typography component="h3" variant="h5">
            Manage Category, Sub-category, Classification
          </Typography>
        </Grid>
        <Grid item xs={4}>
          <Button
            variant="contained"
            sx={{
              float: "right",
              borderRadius: "0.5rem",
              paddingInline: "1.5rem",
            }}
          >
            Delete Checked
          </Button>
        </Grid>
        <Grid item xs={12}>
          <Grid container spacing={5} mt={1}>
            <Grid item xs={4}>
              <Button
                variant="contained"
                fullWidth
                sx={{
                  borderRadius: "0.5rem",
                }}
              >
                + New Category
              </Button>
              {category.map((data) => (
                <Box mt={1}>
                  <FormControlLabel
                    fullWidth
                    control={
                      <Checkbox
                        name={data.name}
                        value={data.name}
                        id={data.id}
                      />
                    }
                    label={data.name}
                  />
                  <Button
                    variant="contained"
                    sx={{
                      borderRadius: "0.5rem",
                      float: "right",
                    }}
                  >
                    <ClearIcon />
                  </Button>
                </Box>
              ))}
            </Grid>
            <Grid item xs={4}>
              <Button
                variant="contained"
                fullWidth
                sx={{
                  borderRadius: "0.5rem",
                }}
              >
                + New Sub Category
              </Button>
              {category.map((data) => (
                <Box mt={1}>
                  <FormControlLabel
                    fullWidth
                    control={
                      <Checkbox
                        name={data.name}
                        value={data.name}
                        id={data.id}
                      />
                    }
                    label={data.name}
                  />
                  <Button
                    variant="contained"
                    sx={{
                      borderRadius: "0.5rem",
                      float: "right",
                    }}
                  >
                    <ClearIcon />
                  </Button>
                </Box>
              ))}
            </Grid>
            <Grid item xs={4}>
              <Button
                variant="contained"
                fullWidth
                sx={{
                  borderRadius: "0.5rem",
                }}
              >
                + New Classification
              </Button>
              {category.map((data) => (
                <Box mt={1}>
                  <FormControlLabel
                    fullWidth
                    control={
                      <Checkbox
                        name={data.name}
                        value={data.name}
                        id={data.id}
                      />
                    }
                    label={data.name}
                  />
                  <Button
                    variant="contained"
                    sx={{
                      borderRadius: "0.5rem",
                      float: "right",
                    }}
                  >
                    <ClearIcon />
                  </Button>
                </Box>
              ))}
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </Fragment>
  );
};

export default ManageCategory;
