/** @format */
import React, { Fragment, useState } from "react";
import {
  TextField,
  Switch,
  FormControlLabel,
  Box,
  Grid,
  Button,
  Typography,
} from "@mui/material";

import { LocalizationProvider, DateRangePicker, DatePicker } from "@mui/lab";
import AdapterDateFns from "@mui/lab/AdapterDateFns";
import axios from "axios";
import SaveIcon from "@mui/icons-material/Save";
import { useToggle } from "../../context/useToggle";
import { CONFIG } from "../../api/MatterApi";

const CreateTask = () => {
  const [value, setValue] = useState(null);
  const [bill, setBill] = useToggle(true);
  const [matterData, setMatterData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isPrivate, setIsPrivate] = useToggle(true);
  const [taskData, setTaskData] = useState({
    matter_no: 0,
    matter_contact: "",
    matter: "",
    billable: true,
    is_private: true,
    task: "",
    due_date: "",
    assign_to: "",
    detail: "",
  });

  const {
    matter_no,
    matter_contact,
    matter,
    billable,
    is_private,
    task,
    due_date,
    assign_to,
    detail,
  } = taskData;

  const FetchData = () => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/api/matter/`, CONFIG)
      .then((res) => {
        console.log(res.data);
        setLoading(false);
        setMatterData(res.data);
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
      });
  };

  const onChange = (e) =>
    setTaskData({ ...taskData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();

    // login(username, email, password);
  };
  return (
    <Fragment>
      <Box
        component="form"
        Validate
        sx={{
          "& .MuiTextField-root": { m: 1 },
        }}
        onSubmit={(e) => onSubmit(e)}
      >
        <Typography component="h3" variant="h4">
          Create New Task
        </Typography>
        <Grid container spacing={2} p={2}>
          <Grid item xs={6}>
            <TextField
              fullWidth
              size="small"
              margin="normal"
              variant="outlined"
              name="matter_no"
              label="Matter #"
              type="number"
              value={matterData.id}
              onChange={(e) => onChange(e)}
              id="matter_no"
              autoComplete="matter_no"
            />
            <TextField
              fullWidth
              size="small"
              margin="normal"
              variant="outlined"
              name="matter"
              label="Matter"
              type="text"
              value={matter}
              onChange={(e) => onChange(e)}
              id="matter"
              autoComplete="matter"
            />
            <TextField
              fullWidth
              size="small"
              margin="normal"
              variant="outlined"
              name="task"
              label="Task"
              type="task"
              value={task}
              onChange={(e) => onChange(e)}
              id="task"
              autoComplete="task"
            />
            <Box ml={2} mt={1}>
              <Button variant="contained">+ Attach File</Button>
            </Box>
          </Grid>
          <Grid item xs={6}>
            <TextField
              fullWidth
              size="small"
              margin="normal"
              variant="outlined"
              name="matter_contact"
              label="Matter Contact"
              type="text"
              value={matter_contact}
              onChange={(e) => onChange(e)}
              id="matter_contact"
              autoComplete="matter_contact"
            />
            <Box p={1}>
              <FormControlLabel
                fullWidth
                size="large"
                label="Billable"
                labelPlacement="start"
                control={
                  <Switch
                    size="large"
                    checked={bill}
                    onChange={() => setBill(bill)}
                  />
                }
              />
              <FormControlLabel
                fullWidth
                size="large"
                label="Private"
                labelPlacement="start"
                control={
                  <Switch
                    size="large"
                    checked={isPrivate}
                    onChange={() => setIsPrivate(isPrivate)}
                  />
                }
              />
            </Box>
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                label="Due Date"
                disablePast
                value={value}
                onChange={(newValue) => {
                  setValue(newValue);
                }}
                renderInput={(params) => <TextField size="small" {...params} />}
              />
            </LocalizationProvider>
            {/* <DatePicker
              fullWidth
              size="small"
              id="due_date"
              label="Due Date"
              disablePast
              type="date"
              InputLabelProps={{
                shrink: true,
              }}
            /> */}
            <TextField
              fullWidth
              size="small"
              margin="normal"
              variant="outlined"
              name="assign_to"
              label="Assign to"
              type="assign_to"
              value={assign_to}
              onChange={(e) => onChange(e)}
              id="assign_to"
              autoComplete="assign_to"
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              fullWidth
              multiline
              rows={5}
              size="small"
              margin="normal"
              variant="outlined"
              name="detail"
              label="Detail"
              type="text"
              value={detail}
              onChange={(e) => onChange(e)}
              id="detail"
            />

            <Button
              variant="contained"
              sx={{ float: "right" }}
              endIcon={<SaveIcon />}
            >
              Save
            </Button>
          </Grid>
        </Grid>
      </Box>
    </Fragment>
  );
};

export default CreateTask;
