/** @format */

import React, { Fragment, useState, useEffect } from "react";

import axios from "axios";

import {
  Button,
  IconButton,
  TextField,
  Checkbox,
  Grid,
  FormControlLabel,
  Tab,
  Paper,
  Box,
} from "@mui/material";
import { TabList, TabContext, TabPanel } from "@mui/lab";
import { useToggle } from "../../context/useToggle";
import { connect } from "react-redux";
import KeyboardArrowRightIcon from "@mui/icons-material/KeyboardArrowRight";

import { update_profile } from "../../actions/profile";

const Profile = ({ user, profile }) => {
  const [open, setOpen] = useToggle(false);
  const [value, setValue] = React.useState("1");

  const [formData, setFormData] = useState({
    username: " ",
    email: " ",
    mobile: 0,
    phone: 0,
    state: "",
    city: "",
    language: "",
    locate: false,
    search_active: false,
    law_school: "",
    grad_year: 0,
    bar_admit_date: "",
    undergrad_school: "",
    undergrad_area: "",
    undergrad_year: "",
    bar_no: "",
    admitted_practice: "",
    practice_time: "",
    longest_tenure: "",
    average_tenure: "",
    current_tenure: "",
    past_bar_companies_no: "",
    primary_area: "",
  });
  const {
    username,
    email,
    mobile,
    phone,
    state,
    city,
    language,
    locate,
    search_active,
    law_school,
    grad_year,
    bar_admit_date,
    undergrad_school,
    undergrad_area,
    undergrad_year,
    bar_no,
    admitted_practice,
    practice_time,
    longest_tenure,
    average_tenure,
    current_tenure,
    past_bar_companies_no,
    primary_area,
  } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();

    update_profile(
      username,
      email,
      mobile,
      phone,
      state,
      city,
      language,
      locate,
      search_active,
      law_school,
      grad_year,
      bar_admit_date,
      undergrad_school,
      undergrad_area,
      undergrad_year,
      bar_no,
      admitted_practice,
      practice_time,
      longest_tenure,
      average_tenure,
      current_tenure,
      past_bar_companies_no,
      primary_area,
    );
  };

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Fragment>
      <Box p={5}>
        <Paper elevation={5} sx={{ borderRadius: 3 }}>
          <TabContext value={value} p={8}>
            <Box sx={{ borderBottom: 1, borderColor: "divider" }}>
              <TabList
                onChange={handleChange}
                aria-label="lab API tabs example"
              >
                <Tab label="profile" value="1" />
                <Tab label="Education" value="2" />
                <Tab label="Work History" value="3" />
              </TabList>
            </Box>
            <TabPanel value="1">
              <Grid container spacing={2}>
                <Grid item sx={12} xs={4}>
                  <TextField
                    margin="normal"
                    fullWidth
                    variant="standard"
                    label="Username"
                    value={username}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.username}
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Mobile #"
                    value={mobile}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.mobile}
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Phone #"
                    value={phone}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.phone}
                  />
                </Grid>
                <Grid item xs={4}>
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Email #"
                    value={email}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.email}
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="State"
                    value={state}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.state}
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="City"
                    value={city}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.city}
                  />
                </Grid>
                <Grid item xs={4}>
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Language"
                    value={language}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.language}
                  />
                  <FormControlLabel
                    fullWidth
                    control={<Checkbox defaultChecked />}
                    label="Willing to locate"
                    labelPlacement="start"
                    value={locate}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.locate}
                  />
                  <FormControlLabel
                    fullWidth
                    control={<Checkbox defaultChecked />}
                    label="Search Type (Active/ Passiv)"
                    labelPlacement="start"
                    value={search_active}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.search_active}
                  />
                  <br />
                  <br />
                  <Button
                    m={5}
                    variant="contained"
                    sx={{
                      float: "right",
                    }}
                    color="success"
                    onClick={() => setValue("2")}
                    endIcon={<KeyboardArrowRightIcon />}
                  >
                    Save & Next
                  </Button>
                </Grid>
              </Grid>
            </TabPanel>
            <TabPanel value="2">
              <Grid container spacing={2}>
                <Grid item xs={4}>
                  <TextField
                    margin="normal"
                    fullWidth
                    variant="standard"
                    label="Law School"
                    value={law_school}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.law_school}
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Grad year"
                    value={grad_year}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.grad_year}
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Bar Admittance Date"
                    value={bar_admit_date}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.bar_admit_date}
                  />
                </Grid>
                <Grid item xs={4}>
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Undergrad school"
                    value={undergrad_school}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.undergrad_school}
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Undergrade area of study"
                    value={undergrad_area}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.undergrad_area}
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Undergrade year"
                    value={undergrad_year}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.undergrad_year}
                  />
                </Grid>
                <Grid item xs={4}>
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Bar"
                    value={bar_no}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.bar_no}
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Admitted practice (state & District)"
                    value={admitted_practice}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.admitted_practice}
                  />
                  <br />
                  <br />
                  <Button
                    variant="contained"
                    mb={5}
                    sx={{
                      float: "right",
                    }}
                    color="success"
                    onClick={() => setValue("3")}
                    endIcon={<KeyboardArrowRightIcon />}
                  >
                    Save & Next
                  </Button>
                </Grid>
              </Grid>
            </TabPanel>
            <TabPanel value="3">
              <Grid container spacing={2}>
                <Grid item xs={4}>
                  <TextField
                    margin="normal"
                    fullWidth
                    variant="standard"
                    label="Practive Time (months / years)"
                    value={practice_time}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.practice_time}
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Longest tenure (months / years)"
                    value={longest_tenure}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.longest_tenure}
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Average tenure (months / years)"
                    value={average_tenure}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.average_tenure}
                  />
                </Grid>
                <Grid item xs={4}>
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Time at Current Company (months / years)"
                    value={current_tenure}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.current_tenure}
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="# of Companies past Bar"
                    value={past_bar_companies_no}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.past_bar_companies_no}
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    variant="standard"
                    label="Primary areas of Law"
                    value={primary_area}
                    onChange={(e) => onChange(e)}
                    defaultValue={profile.primary_area}
                  />
                </Grid>
                <Grid item xs={4}>
                  <Button
                    variant="contained"
                    type="submit"
                    onClick={onSubmit}
                    sx={{
                      float: "right",
                      marginTop: "10rem",
                    }}
                    color="success"
                  >
                    Save
                  </Button>
                </Grid>
              </Grid>
            </TabPanel>
          </TabContext>
        </Paper>
      </Box>
    </Fragment>
  );
};
const mapStateToProps = (state) => ({
  // isAuthenticated: state.auth.isAuthenticated,
  user: state.auth.user,
  profile: state.auth.profile,
});

export default connect(mapStateToProps)(Profile);
