import {
    ROLE_SUCCESS,
    ROLE_FAIL,
    ROLE_LOADED_SUCCESS,
    ROLE_LOADED_FAIL
} from './types';

// get the list of roles by user email
export const get_role = (username) => async dispatch => {
    const config = {
        headers: {
            'Content-Type': 'application/json'
        }
    };

    const body = JSON.stringify({ username });

    try {
        const res = await axios.post(`${process.env.REACT_APP_API_URL}/auth/roles/`, body, config);

        dispatch({
            type: ROLE_LOADED_SUCCESS,
            payload: res.data
        });

        dispatch(load_user());
    } catch (err) {
        dispatch({
            type: ROLE_LOADED_FAIL
        })
    }
};

//  setting roles at the time of registeration, or upgrading account 
export const create_role = (username, is_firm, is_lawyer,  is_client) => async dispatch => {
    const config = {
        headers: {
            'Content-Type': 'application/json'
        }
    };

    const body = JSON.stringify({username, is_firm, is_lawyer, is_client});

    try {
        const res = await axios.post(`${process.env.REACT_APP_API_URL}/auth/roles/`, body, config);
        dispatch({
            type: ROLE_SUCCESS,
            payload: res.data 
        });
    } catch (err) {
        dispatch({
            type: ROLE_FAIL
        })
    }
}
