/** @format */
import React, { Fragment, useState } from "react";
import { TextField, Box, Grid, Button, Typography } from "@mui/material";
import SaveIcon from "@mui/icons-material/Save";

const CreateTask = () => {
  const [taskData, setTaskData] = useState({
    matter_no: 0,
    matter_contact: "",
    matter: "",
    billable: true,
    private: true,
    task: "",
    due: "",
    assign_to: "",
    detail: "",
  });

  // const {
  //   matter_no,
  //   matter_contact,
  //   matter,
  //   billable,
  //   private,
  //   task,
  //   due,
  //   assign_to,
  //   detail,
  // } = taskData;

  const onChange = (e) =>
    setTaskData({ ...taskData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();

    // login(username, email, password);
  };
  return (
    <Fragment>
      <Box
        component="form"
        Validate
        sx={{
          "& .MuiTextField-root": { m: 1 },
        }}
        onSubmit={(e) => onSubmit(e)}
      >
        <Typography component="h3" variant="h4">
          Create New Matter
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={8}>
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="matter_no"
              label="matter_no"
              type="text"
              value={taskData.matter_no}
              onChange={(e) => onChange(e)}
              id="matter_no"
              autoComplete="matter_no"
            />
          </Grid>
        </Grid>
      </Box>
    </Fragment>
  );
};
