/** @format */

import React, { Fragment, useState } from "react";
import { TextField, Box, Grid, Button, Typography } from "@mui/material";
import SaveIcon from "@mui/icons-material/Save";

import { LocalizationProvider, DateRangePicker, DatePicker } from "@mui/lab";
import AdapterDateFns from "@mui/lab/AdapterDateFns";

import { CreateMatter } from "../../api/MatterApi";

const NewMatter = () => {
  // const matter_number = Math.floor(Math.random() * 10000000);
  const matter_number = 23568;
  const [open, setOpen] = useState(null);
  const [when, setWhen] = useState(null);
  const [closed, setClosed] = useState(null);
  const [matterData, setMatterData] = useState({
    contact: 0,
    matter_name: "",
    matter_type: "",
    matter_source: "",
    matter_status: "",
    assign_to: 0,
    assign_by: 0,
    billing_rate: 0.0,
    alerts: "",
    total_days: 0,
    jurisdiction: "",
    status_limitaion: null,
    opposing_counsel: "",
    where: "",
    involved: "",
    witnesses: "",
    narrative: "",
  });

  const {
    contact,
    matter_name,
    matter_type,
    matter_source,
    matter_status,
    assign_to,
    assign_by,
    billing_rate,
    alerts,
    total_days,
    jurisdiction,
    status_limitaion,
    opposing_counsel,
    where,
    involved,
    witnesses,
    narrative,
  } = matterData;

  // const TotalDays = () => {
  //   if (!(open == null & closed == null)){
  //     // To calculate the time difference of two dates
  //     let Difference_In_Time = open.getTime() - closed.getTime();

  //     // To calculate the no. of days between two dates
  //     let Difference_In_Days = Difference_In_Time / (1000 * 3600 * 24);

  //     return Difference_In_Days
  //   }
  // };
  const onChange = (e) =>
    setMatterData({ ...matterData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();

    const status = CreateMatter(
      contact,
      matter_name,
      matter_type,
      matter_source,
      matter_status,
      assign_to,
      assign_by,
      billing_rate,
      alerts,
      open,
      closed,
      total_days,
      jurisdiction,
      status_limitaion,
      opposing_counsel,
      where,
      when,
      involved,
      witnesses,
      narrative,
    );
    console.log(status);
  };
  return (
    <Fragment>
      <Box
        component="form"
        Validate
        sx={{
          "& .MuiTextField-root": { m: 1 },
        }}
        onSubmit={(e) => onSubmit(e)}
      >
        <Typography component="h3" variant="h4">
          Create New Matter
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={8}>
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="contact"
              label="contact"
              type="number"
              value={contact}
              onChange={(e) => onChange(e)}
              id="contact"
              sx={{
                width: "97%",
              }}
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="matter_number"
              label="matter_number"
              type="text"
              value={matter_number}
              // onChange={(e) => onChange(e)}
              id="matter_number"
              disabled
              sx={{
                width: "48%",
              }}
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="matter_name"
              label="matter_name"
              type="text"
              value={matter_name}
              onChange={(e) => onChange(e)}
              id="matter_name"
              sx={{
                width: "47%",
              }}
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="alerts"
              label="alerts"
              type="text"
              value={alerts}
              onChange={(e) => onChange(e)}
              id="alerts"
              autoComplete="alerts"
              sx={{
                width: "97%",
              }}
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="matter_type"
              label="matter_type"
              type="text"
              value={matter_type}
              onChange={(e) => onChange(e)}
              id="matter_type"
              sx={{
                width: "48%",
              }}
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="matter_source"
              label="matter_source"
              type="text"
              value={matter_source}
              onChange={(e) => onChange(e)}
              id="matter_source"
              sx={{
                width: "47%",
              }}
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="matter_status"
              label="matter_status"
              type="text"
              value={matter_status}
              onChange={(e) => onChange(e)}
              id="matter_status"
              sx={{
                width: "48%",
              }}
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="assign_to"
              label="assign_to"
              type="number"
              value={assign_to}
              onChange={(e) => onChange(e)}
              id="assign_to"
              sx={{
                width: "47%",
              }}
            />
          </Grid>
          <Grid item xs={4}>
            <TextField
              size="small"
              fullWidth
              margin="normal"
              variant="outlined"
              name="billing_rate"
              label="billing_rate"
              type="number"
              value={billing_rate}
              onChange={(e) => onChange(e)}
              id="billing_rate"
            />
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                label="Open Date"
                disablePast
                value={open}
                name="open"
                id="open"
                onChange={(e) => setOpen(e)}
                renderInput={(params) => (
                  <TextField fullWidth size="small" {...params} />
                )}
              />
            </LocalizationProvider>
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                label="Close Date"
                disablePast
                value={closed}
                name="closed"
                id="closed"
                onChange={(e) => setClosed(e)}
                renderInput={(params) => (
                  <TextField fullWidth size="small" {...params} />
                )}
              />
            </LocalizationProvider>
            <TextField
              size="small"
              fullWidth
              margin="normal"
              variant="outlined"
              name="total_days"
              label="total_days"
              type="number"
              value={total_days}
              onChange={(e) => onChange(e)}
              id="total_days"
              disabled
            />
          </Grid>

          <Grid item xs={4}>
            <br />
            <Typography component="h5" variant="h6">
              Legal Concerns
            </Typography>
            <br />
            <TextField
              size="small"
              fullWidth
              margin="normal"
              variant="outlined"
              name="jurisdiction"
              label="jurisdiction"
              type="text"
              value={jurisdiction}
              onChange={(e) => onChange(e)}
              id="jurisdiction"
              autoComplete="jurisdiction"
            />
            <TextField
              size="small"
              fullWidth
              margin="normal"
              variant="outlined"
              name="status_limitaion"
              label="status_limitaion"
              type="text"
              value={status_limitaion}
              onChange={(e) => onChange(e)}
              id="status_limitaion"
              autoComplete="status_limitaion"
            />
            <TextField
              size="small"
              fullWidth
              margin="normal"
              variant="outlined"
              name="opposing_counsel"
              label="opposing_counsel"
              type="text"
              value={opposing_counsel}
              onChange={(e) => onChange(e)}
              id="opposing_counsel"
              autoComplete="opposing_counsel"
            />
          </Grid>
          <Grid item xs={4}>
            <Typography component="h5" variant="h6" sx={{ fontSize: "1rem" }}>
              Event Detail
            </Typography>
            <TextField
              size="small"
              fullWidth
              margin="normal"
              variant="outlined"
              name="where"
              label="where"
              type="text"
              value={where}
              onChange={(e) => onChange(e)}
              id="where"
              autoComplete="where"
            />
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                label="When"
                value={when}
                name="when"
                id="when"
                onChange={(e) => setWhen(e)}
                renderInput={(params) => (
                  <TextField fullWidth size="small" {...params} />
                )}
              />
            </LocalizationProvider>
            <TextField
              size="small"
              fullWidth
              margin="normal"
              variant="outlined"
              name="involved"
              label="Who was involved"
              type="text"
              value={involved}
              onChange={(e) => onChange(e)}
              id="involved"
              autoComplete="involved"
            />
            <TextField
              size="small"
              fullWidth
              margin="normal"
              variant="outlined"
              name="witnesses"
              label="witnesses"
              type="text"
              value={witnesses}
              onChange={(e) => onChange(e)}
              id="witnesses"
              autoComplete="witnesses"
            />
          </Grid>
          <Grid item xs={4}>
            <TextField
              size="small"
              fullWidth
              label="Customer Narrative of the Event"
              multiline
              type="text"
              rows={10}
              value={narrative}
              onChange={(e) => onChange(e)}
              defaultValue="Narrative"
              name="narrative"
              id="narrative"
              autoComplete="narrative"
            />
          </Grid>
        </Grid>
        <Button
          variant="contained"
          type="submit"
          sx={{ float: "right" }}
          endIcon={<SaveIcon />}
        >
          Save
        </Button>
      </Box>
    </Fragment>
  );
};

export default NewMatter;
