/** @format */

import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";

import {
  Paper,
  Table,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  FormControlLabel,
  Checkbox,
} from "@mui/material";

const MatterList = () => {
  const [matter, setMatters] = useState(null);
  const config = {
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
    },
  };
  useEffect(() => {
    const res = axios.get(
      `${process.env.REACT_APP_API_URL}/api/matter`,
      config,
    );
    setMatters(res.data);
  }, []);
  return (
    <Fragment>
      <FormGroup aria-label="position" row>
        <FormControlLabel
          control={<Checkbox defaultChecked />}
          label="My Tasks"
        />
        <FormControlLabel
          control={<Checkbox defaultChecked />}
          label="Red Tasks"
        />
        <FormControlLabel
          control={<Checkbox defaultChecked />}
          label="Green Tasks"
        />
        <FormControlLabel
          control={<Checkbox defaultChecked />}
          label="Yellow Tasks"
        />
      </FormGroup>
      <Typography component="h3">All Matters Task's List</Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Matter Tasks</TableCell>
              <TableCell>Matter Name</TableCell>
              <TableCell>Last Action</TableCell>
              <TableCell>Next Action</TableCell>
              <TableCell>Assign To</TableCell>
            </TableRow>

            <TableRow>
              <TableCell></TableCell>
            </TableRow>
          </TableHead>
        </Table>
      </TableContainer>
    </Fragment>
  );
};

export default MatterList;
