/** @format */

import React, { useState } from "react";
import axios from "axios";

export const CONFIG = {
  headers: {
    "Content-Type": "application/json",
    Accept: "application/json",
  },
};

export const create_matter = ({
  contact,
  matter_name,
  matter_type,
  matter_source,
  matter_status,
  assign_to,
  assign_by,
  billing_rate,
  alerts,
  open_date,
  close_date,
  total_days,
  jurisdiction,
  status_limitaion,
  opposing_counsel,
  where,
  when,
  involved,
  witnesses,
  narrative,
}) => {
  const [status, setStatus] = useState("");
  const body = JSON.stringify({
    contact,
    matter_name,
    matter_type,
    matter_source,
    matter_status,
    assign_to,
    assign_by,
    billing_rate,
    alerts,
    open_date,
    close_date,
    total_days,
    jurisdiction,
    status_limitaion,
    opposing_counsel,
    where,
    when,
    involved,
    witnesses,
    narrative,
  });

  try {
    const res = axios.post(
      `${process.env.REACT_APP_API_URL}/api/new-matter/`,
      body,
      CONFIG,
    );
    setStatus(res.statusText);
  } catch (err) {
    setStatus(err.statusTextd);
  }
};

export const retrive_matter = () => {};
