/** @format */

import React, { Fragment, useState } from "react";

import {
  Box,
  FormLabel,
  FormControlLabel,
  Radio,
  RadioGroup,
  FormControl,
  Checkbox,
} from "@mui/material";

const InvoicePayment = () => {
  const [invoiceData, setInvoiceData] = useState({
    payment_type: "",
    date: "",
    retainer: "",
    payment: "",
    date_range: "",
    menu: "",
    bank_account: "",
    payment_method: "",
    invoice_payment: false,
    payment_plan: false,
  });

  const {
    payment_type,
    date,
    retainer,
    payment,
    date_range,
    menu,
    bank_account,
    payment_method,
    invoice_payment,
    payment_plan,
  } = invoiceData;
  const onChange = (e) =>
    setInvoiceData({ ...invoiceData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
  };
  return (
    <Fragment>
      <Box>
        <FormControl component="fieldset">
          <FormLabel component="legend">Payment Method</FormLabel>
          <RadioGroup
            row
            aria-label="payment_type"
            defaultValue="cash"
            name="payment_type"
            onChange={(e) => onChange(e)}
            id="payment_type"
            autoComplete="payment_type"
          >
            <FormControlLabel value="cash" control={<Radio />} label="Cash" />
            <FormControlLabel value="cc" control={<Radio />} label="CC" />
            <FormControlLabel value="wire" control={<Radio />} label="Wire" />
            <FormControlLabel value="check" control={<Radio />} label="Check" />
          </RadioGroup>
        </FormControl>
      </Box>
    </Fragment>
  );
};

export default InvoicePayment;
