/** @format */

import React, { Fragment, useState } from "react";
import {
  Box,
  Typography,
  Grid,
  TextField,
  Table,
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  Checkbox,
  FormControlLabel,
  Switch,
} from "@mui/material";
import {
  LocalizationProvider,
  DateRangePicker,
  MobileDateRangePicker,
  DesktopDateRangePicker,
} from "@mui/lab";
import AdapterDateFns from "@mui/lab/AdapterDateFns";
import Stack from "@mui/material/Stack";

import { useToggle } from "../../context/useToggle";

import { styled } from "@mui/styles";
import { setDate } from "date-fns";

const CreateInvoice = () => {
  const [approvel, setApprovel] = useToggle(false);
  const [invoiceData, setInvoiceData] = useState({
    matter: "",
    contact: "",
    billable: false,
    date_range: "",
    create_date: "",
    due_date: "",
    template: "",
    invoice_no: "",
    discount: "",
    submit: false,
  });

  const {
    matter,
    contact,
    billable,
    date_range,
    create_date,
    due_date,
    template,
    invoice_no,
    discount,
    submit,
  } = invoiceData;

  const [invoice, setInvoice] = useState(null);
  // useEffect(() => {
  //   const config = {
  //     headers: {
  //       "Content-Type": "application/json",
  //       Accept: "application/json",
  //     },
  //   };
  //   const res = axios.get(
  //     `${process.env.REACT_APP_API_URL}/api/category`,
  //     config,
  //   );
  //   setInvoice(res.data);
  // }, []);
  const onChange = (e) =>
    setInvoiceData({ ...invoiceData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
  };
  const [dateRange, setDateRange] = React.useState([null, null]);
  return (
    <Fragment>
      <Box
        component="form"
        Validate
        sx={{
          "& .MuiTextField-root": { m: 1, width: "10rem" },
        }}
        onSubmit={(e) => onSubmit(e)}
      >
        <Typography component="h3" variant="h4">
          Create New Invoice
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField
              margin="normal"
              variant="standard"
              name="matter"
              label="matter"
              type="text"
              value="matter"
              onChange={(e) => onChange(e)}
              id="matter"
              autoComplete="matter"
            />
            <TextField
              margin="normal"
              variant="standard"
              name="contact"
              label="contact"
              type="text"
              value="contact"
              onChange={(e) => onChange(e)}
              id="contact"
              autoComplete="contact"
            />
            <FormControlLabel
              control={
                <Checkbox
                  name="billable"
                  value="billable"
                  onChange={(e) => onChange(e)}
                  id="billable"
                />
              }
              label="Unbillable"
            />
            {/* <TextField
              margin="normal"
              variant="standard"
              name="date_range"
              type="date"
              label="date_range"
              value="date_range"
              onChange={(e) => onChange(e)}
              id="date_range"
              autoComplete="date_range"
            /> */}
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DateRangePicker
                calendars={1}
                value={dateRange}
                onChange={(newValue) => {
                  setDateRange(newValue);
                }}
                renderInput={(startProps, endProps) => (
                  <Fragment>
                    <TextField variant="standard" {...startProps} />
                    <Box sx={{ mx: 2 }}> to </Box>
                    <TextField variant="standard" {...endProps} />
                  </Fragment>
                )}
              />
            </LocalizationProvider>
            <TextField
              margin="normal"
              variant="standard"
              name="create_date"
              type="text"
              value="create_date"
              onChange={(e) => onChange(e)}
              id="create_date"
              autoComplete="create_date"
            />
            <TextField
              margin="normal"
              variant="standard"
              name="due_date"
              type="date"
              value="due_date"
              onChange={(e) => onChange(e)}
              id="due_date"
              autoComplete="due_date"
            />
            <TextField
              margin="normal"
              variant="standard"
              name="template"
              label="template"
              type="text"
              value="template"
              onChange={(e) => onChange(e)}
              id="template"
              autoComplete="template"
            />
            <TextField
              margin="normal"
              variant="standard"
              name="contact"
              label="contact"
              type="text"
              value="contact"
              onChange={(e) => onChange(e)}
              id="contact"
              autoComplete="contact"
            />
            <TextField
              margin="normal"
              variant="standard"
              name="invoice_no"
              label="invoice_no"
              type="text"
              value="invoice_no"
              onChange={(e) => onChange(e)}
              id="invoice_no"
              autoComplete="invoice_no"
            />
            <TextField
              margin="normal"
              variant="standard"
              name="discount"
              label="discount"
              type="text"
              value="discount"
              onChange={(e) => onChange(e)}
              id="discount"
              autoComplete="discount"
            />
            <FormControlLabel
              label="Submit For Approvel"
              control={
                <Switch
                  checked={approvel}
                  onChange={() => setApprovel(approvel)}
                />
              }
            />
          </Grid>
          <Grid item xs={12}>
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Sub-Category</TableCell>
                    <TableCell>Description</TableCell>
                    <TableCell>Date</TableCell>
                    <TableCell>Hours</TableCell>
                    <TableCell>Rate</TableCell>
                    <TableCell>Sub</TableCell>
                    <TableCell>Tax</TableCell>
                    <TableCell>int</TableCell>
                    <TableCell>Disc.</TableCell>
                    <TableCell>Total</TableCell>
                    <TableCell>Biller</TableCell>
                  </TableRow>
                  {invoice?.map((invoice) => {
                    <TableRow id={invoice.id}>
                      <TableCell>
                        <Checkbox />
                      </TableCell>
                      <TableCell>{invoice.sub_cat}</TableCell>
                      <TableCell>{invoice.discription}</TableCell>
                      <TableCell>{invoice.date}</TableCell>
                      <TableCell>{invoice.hours}</TableCell>
                      <TableCell>{invoice.rate}</TableCell>
                      <TableCell>{invoice.sub}</TableCell>
                      <TableCell>{invoice.tax}</TableCell>
                      <TableCell>{invoice.int}</TableCell>
                      <TableCell>{invoice.dics}</TableCell>
                      <TableCell>{invoice.total}</TableCell>
                      <TableCell>{invoice.biller}</TableCell>
                    </TableRow>;
                  })}
                </TableHead>
              </Table>
            </TableContainer>
          </Grid>
          <Grid item></Grid>
        </Grid>
      </Box>
    </Fragment>
  );
};

export default CreateInvoice;
