/** @format */
import React, { Fragment, useState } from "react";
import {
  TextField,
  Switch,
  FormControlLabel,
  Box,
  Grid,
  Button,
  Typography,
} from "@mui/material";

import { LocalizationProvider, DateRangePicker } from "@mui/lab";
import AdapterDateFns from "@mui/lab/AdapterDateFns";

import SaveIcon from "@mui/icons-material/Save";
import { useToggle } from "../../context/useToggle";
const CreateTask = () => {
  const [bill, setBill] = useToggle(true);
  const [isPrivate, setIsPrivate] = useToggle(true);
  const [taskData, setTaskData] = useState({
    matter_no: 0,
    matter_contact: "",
    matter: "",
    billable: true,
    is_private: true,
    task: "",
    due_date: "",
    assign_to: "",
    detail: "",
  });

  const {
    matter_no,
    matter_contact,
    matter,
    billable,
    is_private,
    task,
    due_date,
    assign_to,
    detail,
  } = taskData;

  const onChange = (e) =>
    setTaskData({ ...taskData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();

    // login(username, email, password);
  };
  return (
    <Fragment>
      <Box
        component="form"
        Validate
        sx={{
          "& .MuiTextField-root": { m: 1 },
        }}
        onSubmit={(e) => onSubmit(e)}
      >
        <Typography component="h3" variant="h4">
          Create New Task
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="matter_no"
              label="matter_no"
              type="text"
              value={matter_no}
              onChange={(e) => onChange(e)}
              id="matter_no"
              autoComplete="matter_no"
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="matter"
              label="matter"
              type="text"
              value={matter}
              onChange={(e) => onChange(e)}
              id="matter"
              autoComplete="matter"
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="task"
              label="task"
              type="task"
              value={task}
              onChange={(e) => onChange(e)}
              id="task"
              autoComplete="task"
            />
            <Button variant="contained">+ Attach File</Button>
          </Grid>
          <Grid item xs={6}>
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="matter_contact"
              label="matter_contact"
              type="text"
              value={matter_contact}
              onChange={(e) => onChange(e)}
              id="matter_contact"
              autoComplete="matter_contact"
            />

            <FormControlLabel
              size="large"
              label="Billable"
              labelPlacement="start"
              control={
                <Switch
                  size="large"
                  checked={bill}
                  onChange={() => setBill(bill)}
                />
              }
            />
            <FormControlLabel
              size="large"
              label="Private"
              labelPlacement="start"
              control={
                <Switch
                  size="large"
                  checked={isPrivate}
                  onChange={() => setIsPrivate(isPrivate)}
                />
              }
            />

            <TextField
              size="small"
              id="due_date"
              label="due_date"
              type="date"
              InputLabelProps={{
                shrink: true,
              }}
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="assign_to"
              label="assign_to"
              type="assign_to"
              value={assign_to}
              onChange={(e) => onChange(e)}
              id="assign_to"
              autoComplete="assign_to"
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              fullWidth
              size="small"
              margin="normal"
              variant="outlined"
              name="detail"
              label="detail"
              type="text"
              value={detail}
              onChange={(e) => onChange(e)}
              id="detail"
              autoComplete="detail"
            />

            <Button
              variant="contained"
              sx={{ float: "right" }}
              endIcon={<SaveIcon />}
            >
              Save
            </Button>
          </Grid>
        </Grid>
      </Box>
    </Fragment>
  );
};

export default CreateTask;
