/** @format */

import React, { Fragment, useEffect, useState } from "react";
import axios from "axios";
import { TextField, Box, Grid, Button, Typography } from "@mui/material";
import SaveIcon from "@mui/icons-material/Save";

import { LocalizationProvider, DateRangePicker, DatePicker } from "@mui/lab";
import AdapterDateFns from "@mui/lab/AdapterDateFns";

import { CreateMatter, CONFIG } from "../../api/MatterApi";

import { ActionAlerts, TotalDays, GetDate } from "../../utils/ActionAlerts";

const Search = () => {
  const [searchData, setSearchData] = useState({
    state: "",
    city: "",
    practice_area: "",
    law_school: "",
    grad_year: 0,
    bar_adm_date: 0,
    longest_tenure: 0,
    average_tenure: 0,
    current_tenure: 0,
    undergrad_study: "",
    lamguages: "",
    admitted_state: "",
    relocate: false,
  });

  const {
    state,
    city,
    practice_area,
    law_school,
    grad_year,
    bar_adm_date,
    longest_tenure,
    average_tenure,
    current_tenure,
    undergrad_study,
    lamguages,
    admitted_state,
    relocate,
  } = searchData;

  const onChange = (e) =>
    setSearchData({ ...searchData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <Fragment>
      <Box
        component="form"
        Validate
        sx={{
          "& .MuiTextField-root": { m: 1 },
        }}
        onSubmit={(e) => onSubmit(e)}
      >
        <Typography component="h3" variant="h4">
          Create New Matter
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={4}>
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="state"
              label="state"
              type="number"
              min="0"
              value={state}
              onChange={(e) => onChange(e)}
              id="state"
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="law_school"
              label="law_school"
              type="text"
              value={law_school}
              onChange={(e) => onChange(e)}
              id="law_school"
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="longest_tenure"
              label="longest_tenure"
              type="number"
              value={longest_tenure}
              onChange={(e) => onChange(e)}
              id="longest_tenure"
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="average_tenure"
              label="average_tenure"
              type="number"
              value={average_tenure}
              onChange={(e) => onChange(e)}
              id="average_tenure"
              autoComplete="average_tenure"
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="current_tenure"
              label="current_tenure"
              type="text"
              value={current_tenure}
              onChange={(e) => onChange(e)}
              id="current_tenure"
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="matter_source"
              label="matter_source"
              type="text"
              value={matter_source}
              onChange={(e) => onChange(e)}
              id="matter_source"
              sx={{
                width: "47%",
              }}
            />
          </Grid>
          <Grid item xs={4}>
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="matter_status"
              label="matter_status"
              type="text"
              value={matter_status}
              onChange={(e) => onChange(e)}
              id="matter_status"
              sx={{
                width: "48%",
              }}
            />
            <TextField
              size="small"
              margin="normal"
              variant="outlined"
              name="assign_to"
              label="assign_to"
              type="number"
              value={assign_to}
              onChange={(e) => onChange(e)}
              id="assign_to"
              sx={{
                width: "47%",
              }}
            />
          </Grid>
          <Grid item xs={4}>
            <TextField
              size="small"
              fullWidth
              margin="normal"
              variant="outlined"
              name="billing_rate"
              label="billing_rate"
              step="0.01"
              type="number"
              value={billing_rate}
              onChange={(e) => onChange(e)}
              id="billing_rate"
            />
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                inputFormat="yyyy-MM-dd"
                mask={mask}
                label="Open Date"
                value={openDate}
                name="open"
                id="open"
                onChange={(e) => setOpenDate(e)}
                renderInput={(params) => (
                  <TextField fullWidth size="small" {...params} />
                )}
              />
            </LocalizationProvider>
          </Grid>
          <Grid item xs={4}>
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                inputFormat="yyyy-MM-dd"
                mask={mask}
                label="Close Date"
                value={closeDate}
                name="closed"
                id="closed"
                onChange={(e) => setCloseDate(e)}
                renderInput={(params) => (
                  <TextField fullWidth size="small" {...params} />
                )}
              />
            </LocalizationProvider>
            <TextField
              size="small"
              fullWidth
              margin="normal"
              variant="outlined"
              name="total_days"
              label="total_days"
              type="number"
              value={totalDays}
              onChange={(e) => setTotalDays(TotalDays(openDate, closeDate))}
              id="total_days"
              disabled
            />

            <TextField
              size="small"
              fullWidth
              margin="normal"
              variant="outlined"
              name="jurisdiction"
              label="jurisdiction"
              type="text"
              value={jurisdiction}
              onChange={(e) => onChange(e)}
              id="jurisdiction"
            />
            <Button
              variant="contained"
              type="submit"
              sx={{ float: "right" }}
              endIcon={<SaveIcon />}
            >
              Apply
            </Button>
          </Grid>
        </Grid>
      </Box>
    </Fragment>
  );
};

export default Search;
