/** @format */

import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import {
  Button,
  Box,
  List,
  ListItem,
  ListItemText,
  Typography,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  DialogContentText,
  TextField,
} from "@mui/material";

import ClearIcon from "@mui/icons-material/Clear";
import ActionAlerts from "../../utils/ActionAlerts";
import { PostData } from "../../utils/ApiController";

const CONFIG = {
  headers: {
    "Content-Type": "application/json",
    Accept: "application/json",
  },
};

const Classification = () => {
  const [classification, setClassification] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [status, setStatus] = useState("");
  const [formData, setFormData] = useState({
    cls: "",
  });

  const { cls } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const [open, setOpen] = useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const FetchData = () => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/api/classification/`, CONFIG)
      .then((res) => {
        console.log(res.data);
        setLoading(false);
        setStatus(res.statusText);
        setClassification(res.data);
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
        setError(err.message);
      });
  };
  useEffect(() => {
    FetchData();
  }, []);
  const handleDelete = (id) => {
    axios
      .delete(
        `${process.env.REACT_APP_API_URL}/api/classification/${id}/`,
        CONFIG,
      )
      .then((res) => {
        FetchData();
        return (
          <ActionAlerts
            value={{ status: res.statusText, message: "Success" }}
          />
        );
      });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    const statusCode = PostData(cls);
    FetchData();
  };
  return (
    <Fragment>
      <Button
        variant="contained"
        onClick={handleClickOpen}
        fullWidth
        sx={{
          borderRadius: "0.5rem",
        }}
      >
        + New Classification
      </Button>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">
          {"Add New Classification"}
        </DialogTitle>
        <DialogContent
          component="form"
          Validate
          onSubmit={(e) => handleSubmit(e)}
        >
          <TextField
            required
            fullWidth
            size="small"
            margin="normal"
            variant="outlined"
            name="cls"
            label="Classification Name"
            type="text"
            value={cls}
            onChange={(e) => onChange(e)}
            id="cls"
            autoComplete="cls"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Close</Button>
          <Button type="submit" autoFocus>
            Add
          </Button>
        </DialogActions>
      </Dialog>
      {!loading ? (
        classification.map((data) => (
          <Box mt={1}>
            <List>
              <ListItem disablePadding>
                <ListItemText>{data.name}</ListItemText>
                <Button
                  variant="contained"
                  value={data.id}
                  onClick={() => handleDelete(data.id)}
                  sx={{
                    borderRadius: "0.5rem",
                    float: "right",
                  }}
                >
                  <ClearIcon />
                </Button>
              </ListItem>
            </List>
          </Box>
        ))
      ) : (
        <Typography>Loading ...</Typography>
      )}
    </Fragment>
  );
};

export default Classification;
