/** @format */

import React, { Fragment, useState, useEffect, useCallback } from "react";
import {
  Typography,
  Button,
  Grid,
  Box,
  Checkbox,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  FormControlLabel,
} from "@mui/material";

import ClearIcon from "@mui/icons-material/Clear";
import { Get, DeleteById } from "../../utils/OperationCURD";

const catHandler = (id) => {};

const Category = () => {
  const [category, setCategory] = useState([]);
  const data = Get("category");
  const fetchData = (async) => {
    const data = Get("category");
    return data;
  };

  useEffect(() => {
    setCategory(fetchData.data);
    console.log(category);
  }, []);

  return (
    <Fragment>
      <Button
        variant="contained"
        fullWidth
        sx={{
          borderRadius: "0.5rem",
        }}
      >
        + New Category
      </Button>
      {category.map((data) => (
        <Box mt={1}>
          <List>
            <ListItem disablePadding>
              <ListItemText>{data.name}</ListItemText>
              <Button
                variant="contained"
                onClick={catHandler(data.id)}
                sx={{
                  borderRadius: "0.5rem",
                  float: "right",
                }}
              >
                <ClearIcon />
              </Button>
            </ListItem>
          </List>
        </Box>
      ))}
    </Fragment>
  );
};

export default Category;
