/** @format */

import React, { Fragment, useState, useEffect, useCallback } from "react";
import axios from "axios";
import {
  Typography,
  Button,
  Grid,
  Box,
  Checkbox,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  FormControlLabel,
} from "@mui/material";

import ClearIcon from "@mui/icons-material/Clear";
import { Get, DeleteById } from "../../utils/OperationCURD";

const CONFIG = {
  headers: {
    "Content-Type": "application/json",
    Accept: "application/json",
  },
};

const catHandler = (id) => {};

const Category = () => {
  const [category, setCategory] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(`${process.env.REACT_APP_API_URL}/api/category/`, CONFIG)
        .then((res) => JSON.parse(res))
        .then((data) => {
          setCategory(data);
        });
    };
    fetchData();
  }, []);
  return (
    <Fragment>
      <Button
        variant="contained"
        fullWidth
        sx={{
          borderRadius: "0.5rem",
        }}
      >
        + New Category
      </Button>

      {category.length ? (
        category.map((data) => (
          <Box mt={1}>
            <List>
              <ListItem disablePadding>
                <ListItemText>{data.name}</ListItemText>
                <Button
                  variant="contained"
                  onClick={catHandler(data.id)}
                  sx={{
                    borderRadius: "0.5rem",
                    float: "right",
                  }}
                >
                  <ClearIcon />
                </Button>
              </ListItem>
            </List>
          </Box>
        ))
      ) : (
        <Typography>Loding ... </Typography>
      )}
    </Fragment>
  );
};

export default Category;
