/** @format */

import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";

import {
  Table,
  TableCell,
  TableContainer,
  Paper,
  TableHead,
  TableRow,
  Typography,
} from "@mui/material";

const MatterList = () => {
  const [matter, setMatters] = useState(null);
  const config = {
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
    },
  };
  useEffect(() => {
    const res = axios.get(
      `${process.env.REACT_APP_API_URL}/api/matter`,
      config,
    );
    setMatters(res.data);
  }, []);
  return (
    <Fragment>
      <Typography component="h3">All Matters Task's List</Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Matter Tasks</TableCell>
              <TableCell>Matter Name</TableCell>
              <TableCell>Last Action</TableCell>
              <TableCell>Next Action</TableCell>
              <TableCell>Assign To</TableCell>
            </TableRow>

            <TableRow>
              <TableCell></TableCell>
            </TableRow>
          </TableHead>
        </Table>
      </TableContainer>
    </Fragment>
  );
};

export default MatterList;
