/** @format */

import React, { Fragment } from "react";
import {
  Box,
  Switch,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Typography,
  FormControlLabel,
} from "@mui/material";
import { LinkButton } from "../../styles/styles";
import SearchIcon from "@mui/icons-material/Search";

import { styled } from "@mui/material/styles";

import { useToggle } from "../../context/useToggle";

const Filters = () => {
  const [paid, setPaid] = useToggle(false);

  const [age, setAge] = React.useState("");

  const handleChange = (event) => {
    setAge(event.target.value);
  };
  const Search = styled("div")(({ theme }) => ({
    position: "",
    borderRadius: "1.5rem",
    backgroundColor: "#eee",
    "&:hover": {
      backgroundColor: "#ddd",
    },
    marginRight: theme.spacing(2),
    marginLeft: 0,
    width: "20%",
  }));

  const SearchIconWrapper = styled("div")(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: "100%",
    position: "absolute",
    pointerEvents: "none",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  }));

  const StyledInputBase = styled(InputBase)(({ theme }) => ({
    color: "inherit",
    "& .MuiInputBase-input": {
      padding: theme.spacing(1, 1, 1, 0),
      // vertical padding + font size from searchIcon
      paddingLeft: `calc(1em + ${theme.spacing(4)})`,
      transition: theme.transitions.create("width"),

      [theme.breakpoints.up("md")]: {
        width: "20ch",
      },
    },
  }));

  return (
    <Fragment>
      <Box
        p={3}
        sx={{
          "& .MuiTextField-root": { m: 1 },
        }}
      >
        <LinkButton to="/create-invoice">Create Invoice</LinkButton>
        <LinkButton to="/create-invoice">Receive Money</LinkButton>
        <FormControlLabel
          label=" Paid/ Unpaid"
          control={<Switch checked={paid} onChange={() => setPaid(paid)} />}
        />
        <Search>
          <SearchIconWrapper>
            <SearchIcon />
          </SearchIconWrapper>
          <StyledInputBase
            placeholder="Searchâ€¦"
            inputProps={{ "aria-label": "search" }}
          />
        </Search>
        <FormControl sx={{ width: "8rem", m: 2 }}>
          <InputLabel id="demo-simple-select-label">All Invoice</InputLabel>
          <Select
            size="small"
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={age}
            label="Age"
            onChange={handleChange}
          >
            <MenuItem value={10}>Ten</MenuItem>
            <MenuItem value={20}>Twenty</MenuItem>
            <MenuItem value={30}>Thirty</MenuItem>
          </Select>
        </FormControl>
        <FormControl sx={{ width: "8rem", m: 2 }}>
          <InputLabel id="demo-simple-select-label">Bill Date</InputLabel>
          <Select
            size="small"
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={age}
            label="Age"
            onChange={handleChange}
          >
            <MenuItem value={10}>Ten</MenuItem>
            <MenuItem value={20}>Twenty</MenuItem>
            <MenuItem value={30}>Thirty</MenuItem>
          </Select>
        </FormControl>
        <FormControl sx={{ width: "8rem", m: 2 }}>
          <InputLabel id="demo-simple-select-label">All Times</InputLabel>
          <Select
            size="small"
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={age}
            label="Age"
            onChange={handleChange}
          >
            <MenuItem value={10}>Ten</MenuItem>
            <MenuItem value={20}>Twenty</MenuItem>
            <MenuItem value={30}>Thirty</MenuItem>
          </Select>
        </FormControl>
        <Typography component="span" variant="h8" sx={{ width: "8rem", m: 2 }}>
          {" "}
          Status:{" "}
        </Typography>

        <FormControl sx={{ width: "8rem", m: 2 }}>
          <InputLabel id="demo-simple-select-label">Any </InputLabel>
          <Select
            size="small"
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={age}
            label="Age"
            onChange={handleChange}
          >
            <MenuItem value={10}>Ten</MenuItem>
            <MenuItem value={20}>Twenty</MenuItem>
            <MenuItem value={30}>Thirty</MenuItem>
          </Select>
        </FormControl>
        <FormControl sx={{ width: "8rem", m: 2 }}>
          <InputLabel id="demo-simple-select-label">Dropd-own</InputLabel>
          <Select
            size="small"
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={age}
            label="Age"
            onChange={handleChange}
          >
            <MenuItem value={10}>Ten</MenuItem>
            <MenuItem value={20}>Twenty</MenuItem>
            <MenuItem value={30}>Thirty</MenuItem>
          </Select>
        </FormControl>
      </Box>
    </Fragment>
  );
};

export default Filters;
