/** @format */

import React, { useState, useEffect, useContext, Fragment } from 'react';

import { Button, Box, Grid, Paper } from '@mui/material';

import {
  TodayRounded,
  TrendingUp,
  KeyboardArrowDown,
  Task,
  MoneyOff,
  MonetizationOn,
  FileCopy,
  HourglassBottom,
  AssignmentTurnedIn,
  Map,
  AddCircle,
  TodayOutlined,
} from '@mui/icons-material';

import { useToggle } from '../../context/useToggle';

import { SideBarBtn } from '../../styles/styles';
// import { UserContext } from '../../context/User';

import { connect } from 'react-redux';
import AdminDashboard from './AdminDashboard';
import ClientDashboard from './ClientDashboard';
import LawyerDashboard from './LawyerDashboard';
import FirmDashboard from './FirmDashboard';
import Sidebar from '../Sidebar/Sidebar';

// import DashboardNavbar from '../Navbars/DashboardNavbar';

const Dashboard = ({ user }) => {
  const [revToggle, setRevToggle] = useToggle(true);
  const [empToggle, setEmpToggle] = useToggle(true);
  const [profileToggle, setProfileToggle] = useToggle(false);

  // checker type of user
  const [localuser, setLocalUser] = useState({
    username: 'Guest',
    is_firm: false,
    is_lawyer: false,
    is_client: true,
  });

  useEffect(() => {
    if (user) {
      setLocalUser({
        username: user.username,
        is_firm: user.is_firm,
        is_lawyer: user.is_lawyer,
        is_client: user.is_client,
      });
    }
  }, [user]);

  const dashboardRedirect = () => {
    if (localuser.is_firm) {
      return <FirmDashboard />;
    } else if (localuser.is_client) {
      return <ClientDashboard />;
    } else if (localuser.is_lawyer) {
      return <LawyerDashboard />;
    } else {
      return <AdminDashboard />;
    }
  };
  // return <>{dashboardRedirect()}</>;
  return (
    <>
      <Grid container mt={3} mx={3} spacing={2}>
        <Grid item xs={2}>
          <Paper elevation={3}>
            {/* <Button
          variant="text"
          fullWidth
          endIcon={<KeyboardArrowDown />}
          sx={{ padding: '0.3em', marginTop: '0.5em', textTransform: 'none' }}
          onClick={() => setRevToggle(revToggle)}
        >
          Components
        </Button> */}

            {/* {revToggle ? (
          <Box>
            <SideBarBtn
              variant="outlined"
              // onClick={() => setCalendarToggle(calendarToggle)}
            >
              <Grid>
                <Grid item>
                  <TodayRounded fontSize="medium" />
                </Grid>
                <Grid item>Calendar</Grid>
              </Grid>
            </SideBarBtn>
            <SideBarBtn
              variant="outlined"
              // onClick={() => setTasksToggle(tasksToggle)}
            >
              <Grid>
                <Grid item>
                  <Task fontSize="medium" />
                </Grid>
                <Grid item>Tasks</Grid>
              </Grid>
            </SideBarBtn>
            <SideBarBtn
              variant="outlined"
              // onClick={() => setProgressToggle(progressToggle)}
            >
              <Grid>
                <Grid item>
                  <TrendingUp fontSize="medium" />
                </Grid>
                <Grid item>Progress</Grid>
              </Grid>
            </SideBarBtn>
          </Box>
        ) : null} */}
            <Button
              variant="text"
              fullWidth
              endIcon={<KeyboardArrowDown />}
              sx={{
                padding: '0.5em',
                marginTop: '0.5em',
                textTransform: 'none',
              }}
              // onClick={() => setEmpToggle(empToggle)}
            >
              Employee Performance
            </Button>
            {empToggle ? (
              <Box>
                <SideBarBtn variant="outlined">
                  <Grid>
                    <Grid item>
                      <AddCircle fontSize="medium" />
                    </Grid>
                    <Grid item>Profile</Grid>
                  </Grid>
                </SideBarBtn>
                <SideBarBtn variant="outlined">
                  <Grid>
                    <Grid item>
                      <Map fontSize="medium" />
                    </Grid>
                    <Grid item>Incovice</Grid>
                  </Grid>
                </SideBarBtn>
                <SideBarBtn variant="outlined">
                  <Grid>
                    <Grid item>
                      <AssignmentTurnedIn fontSize="medium" />
                    </Grid>
                    <Grid item>Matters</Grid>
                  </Grid>
                </SideBarBtn>
                <SideBarBtn variant="outlined">
                  <Grid>
                    <Grid item>
                      <HourglassBottom fontSize="medium" />
                    </Grid>
                    <Grid item>Billable Hour</Grid>
                  </Grid>
                </SideBarBtn>
                <SideBarBtn variant="outlined">
                  <Grid>
                    <Grid item>
                      <FileCopy fontSize="medium" />
                    </Grid>
                    <Grid item>Billable v Non-Billable</Grid>
                  </Grid>
                </SideBarBtn>
                <SideBarBtn variant="outlined">
                  <Grid>
                    <Grid item>
                      <MoneyOff fontSize="medium" />
                    </Grid>
                    <Grid item>Spending</Grid>
                  </Grid>
                </SideBarBtn>
                <SideBarBtn variant="outlined">
                  <Grid>
                    <Grid item>
                      <MonetizationOn fontSize="medium" />
                    </Grid>
                    <Grid item>Profit & Lost</Grid>
                  </Grid>
                </SideBarBtn>
                <SideBarBtn variant="outlined">
                  <Grid>
                    <Grid item>
                      <Task fontSize="medium" />
                    </Grid>
                    <Grid item>Tasks</Grid>
                  </Grid>
                </SideBarBtn>
              </Box>
            ) : null}
          </Paper>
        </Grid>
        <Grid item xs={10}>
          {dashboardRedirect()}
        </Grid>
      </Grid>
    </>
  );
};
const mapStateToProps = (state) => ({
  // isAuthenticated: state.auth.isAuthenticated
  user: state.auth.user,
});

export default connect(mapStateToProps)(Dashboard);
