/** @format */

import React, { useState, Fragment } from 'react';
// import axios from 'axios';

import { connect } from 'react-redux';

import { Box, Button, Paper, Grid, IconButton, Tab } from '@mui/material';
import {} from '@mui/lab';
import { SideBarBtn } from '../../styles/styles';
import {
  TodayRounded,
  TrendingUp,
  KeyboardArrowDown,
  Task,
  MoneyOff,
  MonetizationOn,
  FileCopy,
  HourglassBottom,
  AssignmentTurnedIn,
  Map,
  AddCircle,
  TodayOutlined,
  PaymentRounded,
  ArrowBackIos,
  ArrowForwardIos,
} from '@mui/icons-material';

import Calender from './components/Calender';
import Tasks from './components/Tasks';
import ProgressBar from './components/ProgressBar';

import { useToggle } from '../../context/useToggle';
import Cards from './components/Cards';
import { cardData } from '../../data/data';

const FirmDashboard = () => {
  const [revToggle, setRevToggle] = useToggle(true);
  const [empToggle, setEmpToggle] = useToggle(true);
  const [cardsToggle, setCardsToggle] = useToggle(true);
  const [sidebarToggle, setSidebarToggle] = useToggle(true);

  const [calendarToggle, setCalendarToggle] = useToggle(true);
  const [tasksToggle, setTasksToggle] = useToggle(true);
  const [progressToggle, setProgressToggle] = useToggle(true);

  return (
    <>
      <Grid item xs={2}>
        <Paper elevation={3}>
          <IconButton
            color="primary"
            aria-label="upload picture"
            component="span"
            onClick={() => setSidebarToggle(sidebarToggle)}
            sx={
              sidebarToggle
                ? {
                    float: 'right',
                    padding: '0.3rem',
                    margin: '0.5rem',
                  }
                : {
                    minHeight: '87vh',
                    float: 'left',
                    padding: '0.3rem',
                    marginTop: '0',
                  }
            }
          >
            {sidebarToggle ? <ArrowBackIos /> : <ArrowForwardIos />}
          </IconButton>
          {sidebarToggle ? (
            <Fragment
              sx={{
                display: 'flex',
                alignItems: 'center',
                '& > :not(style)': { m: 1 },
              }}
            >
              <Button
                variant="text"
                fullWidth
                endIcon={<KeyboardArrowDown />}
                sx={{
                  padding: '0.3em',
                  marginTop: '0.5em',
                  textTransform: 'none',
                }}
                onClick={() => setRevToggle(revToggle)}
              >
                Components
              </Button>
              {revToggle ? (
                <Box>
                  <SideBarBtn
                    variant="outlined"
                    onClick={() => setCardsToggle(cardsToggle)}
                  >
                    <Grid>
                      <Grid item>
                        <PaymentRounded fontSize="medium" />
                      </Grid>
                      <Grid item>Cards</Grid>
                    </Grid>
                  </SideBarBtn>
                  <SideBarBtn
                    variant="outlined"
                    onClick={() => setCalendarToggle(calendarToggle)}
                  >
                    <Grid>
                      <Grid item>
                        <TodayRounded fontSize="medium" />
                      </Grid>
                      <Grid item>Calendar</Grid>
                    </Grid>
                  </SideBarBtn>
                  <SideBarBtn
                    variant="outlined"
                    onClick={() => setTasksToggle(tasksToggle)}
                  >
                    <Grid>
                      <Grid item>
                        <Task fontSize="medium" />
                      </Grid>
                      <Grid item>Tasks</Grid>
                    </Grid>
                  </SideBarBtn>
                  <SideBarBtn
                    variant="outlined"
                    onClick={() => setProgressToggle(progressToggle)}
                  >
                    <Grid>
                      <Grid item>
                        <TrendingUp fontSize="medium" />
                      </Grid>
                      <Grid item>Progress</Grid>
                    </Grid>
                  </SideBarBtn>
                </Box>
              ) : null}
              <Button
                variant="text"
                fullWidth
                endIcon={<KeyboardArrowDown />}
                sx={{
                  padding: '0.5em',
                  marginTop: '0.5em',
                  textTransform: 'none',
                }}
                onClick={() => setEmpToggle(empToggle)}
              >
                Employee Performance
              </Button>
              {empToggle ? (
                <Box>
                  <SideBarBtn variant="outlined">
                    <Grid>
                      <Grid item>
                        <AddCircle fontSize="medium" />
                      </Grid>
                      <Grid item>New Accounts</Grid>
                    </Grid>
                  </SideBarBtn>
                  <SideBarBtn variant="outlined">
                    <Grid>
                      <Grid item>
                        <Map fontSize="medium" />
                      </Grid>
                      <Grid item>Map</Grid>
                    </Grid>
                  </SideBarBtn>
                  <SideBarBtn variant="outlined">
                    <Grid>
                      <Grid item>
                        <AssignmentTurnedIn fontSize="medium" />
                      </Grid>
                      <Grid item>Task Completed</Grid>
                    </Grid>
                  </SideBarBtn>
                  <SideBarBtn variant="outlined">
                    <Grid>
                      <Grid item>
                        <HourglassBottom fontSize="medium" />
                      </Grid>
                      <Grid item>Billable Hour</Grid>
                    </Grid>
                  </SideBarBtn>
                  <SideBarBtn variant="outlined">
                    <Grid>
                      <Grid item>
                        <FileCopy fontSize="medium" />
                      </Grid>
                      <Grid item>Billable v Non-Billable</Grid>
                    </Grid>
                  </SideBarBtn>
                  <SideBarBtn variant="outlined">
                    <Grid>
                      <Grid item>
                        <MoneyOff fontSize="medium" />
                      </Grid>
                      <Grid item>Spending</Grid>
                    </Grid>
                  </SideBarBtn>
                  <SideBarBtn variant="outlined">
                    <Grid>
                      <Grid item>
                        <MonetizationOn fontSize="medium" />
                      </Grid>
                      <Grid item>Profit & Lost</Grid>
                    </Grid>
                  </SideBarBtn>
                  <SideBarBtn variant="outlined">
                    <Grid>
                      <Grid item>
                        <Task fontSize="medium" />
                      </Grid>
                      <Grid item>Tasks</Grid>
                    </Grid>
                  </SideBarBtn>
                </Box>
              ) : null}
            </Fragment>
          ) : null}
        </Paper>
      </Grid>

      {/*main dashboard section for display  */}
      <Grid item xs={10}>
        <Paper elevation={3} sx={{ minHeight: '84vh' }}>
          <Grid container px={2}>
            {cardsToggle ? (
              <Grid item xs={12} mt={2}>
                {cardData.map((data, i) => (
                  <Cards key={i} data={data} />
                ))}
              </Grid>
            ) : null}
            {calendarToggle ? (
              <Grid item xs={4}>
                <Calender />
              </Grid>
            ) : null}
            {tasksToggle ? (
              <Grid item mt={3} p={2} xs={8}>
                <Tasks />
              </Grid>
            ) : null}
          </Grid>
        </Paper>
      </Grid>
    </>
  );
};

const mapStateToProps = (state) => ({
  // isAuthenticated: state.auth.isAuthenticated
  user: state.auth.user,
});
export default connect(mapStateToProps)(FirmDashboard);
