/** @format */

import React, { Fragment, useState, useEffect, useCallback } from "react";
import axios from "axios";
import {
  Typography,
  Button,
  Grid,
  Box,
  Checkbox,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  FormControlLabel,
} from "@mui/material";
import ClearIcon from "@mui/icons-material/Clear";

import { Get, DeleteById } from "../../utils/OperationCURD";
const CONFIG = {
  headers: {
    "Content-Type": "application/json",
    Accept: "application/json",
  },
};

const ManageCategory = () => {
  const [category, setCategory] = useState([]);
  const [subCategory, setSubCategory] = useState([]);
  const [classification, setClassification] = useState([]);

  const cat = Get("category");
  const subcat = Get("sub-category");
  const cls = Get("classification");

  useEffect(() => {
    setCategory(cat.data);
    setSubCategory(subcat.data);
    setClassification(cls.data);
  }, []);

  const subCatHandler = (id) => {
    const link = "sub-category";
    DeleteById(link, id);
  };

  return (
    <Fragment>
      <Grid container>
        <Grid item xs={8}>
          <Typography component="h3" variant="h5">
            Manage Category, Sub-category, Classification
          </Typography>
        </Grid>

        <Grid item xs={12}>
          <Grid container spacing={5} mt={1}>
            <Grid item xs={4}>
              <Button
                variant="contained"
                fullWidth
                sx={{
                  borderRadius: "0.5rem",
                }}
              >
                + New Category
              </Button>
              {category.map((data) => (
                <Box mt={1}>
                  <List>
                    <ListItem disablePadding>
                      <ListItemText>{data.name}</ListItemText>
                      <Button
                        variant="contained"
                        onClick={catHandler(data.id)}
                        sx={{
                          borderRadius: "0.5rem",
                          float: "right",
                        }}
                      >
                        <ClearIcon />
                      </Button>
                    </ListItem>
                  </List>
                </Box>
              ))}
            </Grid>
            <Grid item xs={4}>
              <Button
                variant="contained"
                fullWidth
                sx={{
                  borderRadius: "0.5rem",
                }}
              >
                + New Sub Category
              </Button>
              {subCategory.map((data) => (
                <Box mt={1}>
                  <List>
                    <ListItem disablePadding>
                      <ListItemText>{data.name}</ListItemText>
                      <Button
                        variant="contained"
                        onClick={subCatHandler(data.id)}
                        sx={{
                          borderRadius: "0.5rem",
                          float: "right",
                        }}
                      >
                        <ClearIcon />
                      </Button>
                    </ListItem>
                  </List>
                </Box>
              ))}
            </Grid>
            <Grid item xs={4}>
              <Button
                variant="contained"
                fullWidth
                sx={{
                  borderRadius: "0.5rem",
                }}
              >
                + New Classification
              </Button>
              {classification.map((data) => (
                <Box mt={1}>
                  <List>
                    <ListItem disablePadding>
                      <ListItemText>{data.name}</ListItemText>
                      <Button
                        variant="contained"
                        sx={{
                          borderRadius: "0.5rem",
                          float: "right",
                        }}
                      >
                        <ClearIcon />
                      </Button>
                    </ListItem>
                  </List>
                </Box>
              ))}
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </Fragment>
  );
};

export default ManageCategory;
