/** @format */

import React, { Fragment, useState, useEffect } from "react";
import {
  Table,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TableBody,
  Typography,
  Checkbox,
  Box,
} from "@mui/material";
import axios from "axios";
import { CONFIG } from "../../api/MatterApi";
import { ActionAlerts } from "../../utils/ActionAlerts";

const LedgerActivity = () => {
  const [activityData, setActivityData] = useState([]);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [status, setStatus] = useState("");
  const FetchData = () => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/api/add-time/`, CONFIG)
      .then((res) => {
        setLoading(false);
        setStatus(res.statusText);
        setActivityData(res.data);
      })
      .catch((err) => {
        setLoading(false);
        setStatus(err.statusText);
      });
  };
  useEffect(() => {
    FetchData();
  }, []);
  return (
    <Fragment>
      <Box>
        <Typography component="h4" variant="h5" mb={2}>
          Activity
        </Typography>

        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Date</TableCell>
                <TableCell>Files</TableCell>
                <TableCell>Cateagory</TableCell>
                <TableCell>Sub-Category</TableCell>
                <TableCell>Notes</TableCell>
                <TableCell>Source</TableCell>
                <TableCell>User</TableCell>
                <TableCell>In/Out</TableCell>
              </TableRow>
              {activityData?.map((data) => {
                return (
                  <TableBody id={data.id}>
                    <TableCell>
                      <Checkbox />
                    </TableCell>
                    <TableCell>{data.date}</TableCell>
                    <TableCell>x</TableCell>
                    <TableCell>{data.category}</TableCell>
                    <TableCell>{data.sub_category}</TableCell>
                    <TableCell>{data.notes}</TableCell>
                    <TableCell>{data.billable}</TableCell>
                    <TableCell>{data.user}</TableCell>
                    <TableCell>{data.inout}</TableCell>
                  </TableBody>
                );
              })}
            </TableHead>
          </Table>
        </TableContainer>
      </Box>
    </Fragment>
  );
};

export default LedgerActivity;
