/** @format */

import axios from "axios";
import React, { useState, useEffect } from "react";

const CONFIG = {
  headers: {
    "Content-Type": "application/json",
    Accept: "application/json",
  },
};

// get request
export function Get(link) {
  const [data, setData] = useState([]);
  const [status, setStatus] = useState(0);
  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/api/${link}/`, CONFIG)
      .then((res) => {
        setData(res.data);
        setStatus(res.status);
      })
      .catch((err) => {
        console.log("error is this", err);
        setStatus(err.status);
      });
  }, []);
  return { data };
}

// get by id request
export const GetById = (link, id) => (async) => {
  const [data, setData] = useState([]);
  const [status, setStatus] = useState(0);

  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/api/${link}/${id}/`, CONFIG)
      .then((res) => {
        setData(res.data);
        setStatus(res.status);
      })
      .catch((err) => {
        console.log("error is this", err);
        setStatus(err.status);
      });
  }, []);
  return { data, status };
};
// delete by id
export const DeleteById = (link, id) => (async) => {
  const [status, setStatus] = useState(0);
  useEffect(() => {
    axios
      .delete(`${process.env.REACT_APP_API_URL}/api/${link}/${id}/`, CONFIG)
      .then((res) => {
        setStatus(res.status);
      })
      .catch((err) => {
        console.log("error is this", err);

        setStatus(err.status);
      });
  }, []);
  return { status };
};
