/** @format */

import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import {
  Button,
  Box,
  List,
  ListItem,
  ListItemText,
  Typography,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  DialogContentText,
} from "@mui/material";

import ClearIcon from "@mui/icons-material/Clear";
import ActionAlerts from "../../utils/ActionAlerts";

const CONFIG = {
  headers: {
    "Content-Type": "application/json",
    Accept: "application/json",
  },
};

const Classification = () => {
  const [classification, setClassification] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [status, setStatus] = useState("");

  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const FetchData = () => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/api/classification/`, CONFIG)
      .then((res) => {
        console.log(res.data);
        setLoading(false);
        setStatus(res.statusText);
        setClassification(res.data);
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
        setError(err.message);
      });
  };
  useEffect(() => {
    FetchData();
  }, []);
  const clsHandler = (id) => {
    axios
      .delete(
        `${process.env.REACT_APP_API_URL}/api/classification/${id}/`,
        CONFIG,
      )
      .then((res) => {
        FetchData();
        return (
          <ActionAlerts
            value={{ status: res.statusText, message: "Success" }}
          />
        );
      });
  };

  return (
    <Fragment>
      <Button
        variant="contained"
        onClick={handleClickOpen}
        fullWidth
        sx={{
          borderRadius: "0.5rem",
        }}
      >
        + New Classification
      </Button>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">
          {"Add New Classification"}
        </DialogTitle>
        <DialogContent>
          <TextField id="outlined-basic" label="Outlined" variant="outlined" />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Disagree</Button>
          <Button onClick={handleClose} autoFocus>
            Agree
          </Button>
        </DialogActions>
      </Dialog>
      {!loading ? (
        classification.map((data) => (
          <Box mt={1}>
            <List>
              <ListItem disablePadding>
                <ListItemText>{data.name}</ListItemText>
                <Button
                  variant="contained"
                  value={data.id}
                  onClick={() => clsHandler(data.id)}
                  sx={{
                    borderRadius: "0.5rem",
                    float: "right",
                  }}
                >
                  <ClearIcon />
                </Button>
              </ListItem>
            </List>
          </Box>
        ))
      ) : (
        <Typography>Loading ...</Typography>
      )}
    </Fragment>
  );
};

export default Classification;
