/** @format */

import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import {
  Typography,
  Button,
  Grid,
  Checkbox,
  FormControlLabel,
} from "@mui/material";

const ManageCategory = () => {
  const [category, setCategory] = useState(null);
  const [subCategory, setSubCategory] = useState(null);
  const [classification, setClassification] = useState(null);

  const [catId, setCatId] = useState(0);
  const [subId, setSubId] = useState(0);
  const [classId, setClassId] = useState(0);

  useEffect(() => {
    const config = {
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    };
    axios
      .get(`${process.env.REACT_APP_API_URL}/api/category/`, config)
      .then((res) => {
        const data = res.data;
        console.log(data);
        setCategory(data);
        console.log(category);
      });

    const sub_cat = axios.get(
      `${process.env.REACT_APP_API_URL}/api/sub-category`,
      config,
    );
    setSubCategory(sub_cat.data);
    const cls = axios.get(
      `${process.env.REACT_APP_API_URL}/api/classification`,
      config,
    );

    setClassification(cls.data);
  }, []);

  const get_Category = category.map((data) => (
    <FormControlLabel
      control={
        <Checkbox
          name={data.name}
          value={data.name}
          // onChange={(e) => set(e)}
          id={data.id}
        />
      }
      label={data.name}
    />
  ));

  return (
    <Fragment>
      <Grid container>
        <Grid item xs={8}>
          <Typography component="h3" variant="h5">
            Manage Category, Sub-category, Classification
          </Typography>
        </Grid>
        <Grid item xs={4}>
          <Button
            variant="contained"
            sx={{
              float: "right",
              borderRadius: "0.5rem",
              paddingInline: "1.5rem",
            }}
          >
            Delete Checked
          </Button>
        </Grid>
        <Grid item xs={12}>
          <Grid container spacing={5} mt={1}>
            <Grid item xs={4}>
              <Button
                variant="contained"
                fullWidth
                sx={{
                  borderRadius: "0.5rem",
                }}
              >
                + New Category
              </Button>
              {get_Category()}
            </Grid>
            <Grid item xs={4}>
              <Button
                variant="contained"
                fullWidth
                sx={{
                  borderRadius: "0.5rem",
                }}
              >
                + New Sub Category
              </Button>
            </Grid>
            <Grid item xs={4}>
              <Button
                variant="contained"
                fullWidth
                sx={{
                  borderRadius: "0.5rem",
                }}
              >
                + New Classification
              </Button>
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </Fragment>
  );
};

export default ManageCategory;
